import React, { useRef, useState } from 'react';
import Webcam from 'react-webcam';

function WebcamCapture() {
  const webcamRef = useRef(null);
  const [camOn, setCamOn] = useState(false);
  const [screenshot, setScreenshot] = useState(null);

  const startWebcam = () => setCamOn(true);
  const stopWebcam = () => setCamOn(false);

  const captureImage = () => {
    if (webcamRef.current) {
      const imageSrc = webcamRef.current.getScreenshot();
      setScreenshot(imageSrc);
      stopWebcam(); // turn off webcam after capture
    }
  };

  const sendToBackend = async () => {
    if (!screenshot) return;

    const blob = await fetch(screenshot).then((res) => res.blob());
    const formData = new FormData();
    formData.append('image', blob, 'capture.jpg');

    fetch("http://localhost:54301/analyze", {
      method: "POST",
      body: formData,
    });
  };

  return (
    <div>
      <h2>Webcam Capture</h2>
      {!camOn && <button onClick={startWebcam}>Start Webcam</button>}
      {camOn && (
        <>
          <Webcam
            audio={false}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            width={400}
          />
          <br />
          <button onClick={captureImage}>Capture Image</button>
          <button onClick={stopWebcam}>Stop Webcam</button>
        </>
      )}
      {screenshot && (
        <>
          <img src={screenshot} alt="Captured" style={{ width: 200 }} />
          <br />
          <button onClick={sendToBackend}>Analyze</button>
        </>
      )}
    </div>
  );
}

export default WebcamCapture;
