import React, { useRef, useState } from 'react';

const WebcamCapture = () => {
  const videoRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const [recording, setRecording] = useState(false);
  const [recordedChunks, setRecordedChunks] = useState([]);
  const [result, setResult] = useState(null);

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    videoRef.current.srcObject = stream;

    const options = { mimeType: "video/webm" };
    const mediaRecorder = new MediaRecorder(stream, options);

    mediaRecorder.ondataavailable = (e) => {
      if (e.data.size > 0) {
        setRecordedChunks((prev) => [...prev, e.data]);
      }
    };

    mediaRecorder.onstop = () => {
      stream.getTracks().forEach(track => track.stop());
    };

    mediaRecorderRef.current = mediaRecorder;
    mediaRecorder.start();
    setRecording(true);
    setRecordedChunks([]);
  };

  const stopRecording = () => {
    mediaRecorderRef.current.stop();
    setRecording(false);
  };

  const uploadRecording = async () => {
    const blob = new Blob(recordedChunks, { type: "video/webm" });
    const formData = new FormData();
    formData.append("file", blob, "recorded_video.webm");

    try {
      const response = await fetch("http://localhost:54301/analyze", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setResult(data);
    } catch (error) {
      console.error("Upload error:", error);
      alert("Failed to upload video.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>ðŸ“· Webcam Video Recorder</h2>
      <video ref={videoRef} autoPlay muted style={{ width: '400px', border: '1px solid gray' }} />

      <div style={{ marginTop: '10px' }}>
        {!recording ? (
          <button onClick={startRecording}>Start Recording</button>
        ) : (
          <button onClick={stopRecording}>Stop Recording</button>
        )}

        {recordedChunks.length > 0 && (
          <button onClick={uploadRecording} style={{ marginLeft: '10px' }}>
            Upload for Analysis
          </button>
        )}
      </div>

      {result && result.posture_feedback && (
        <div style={{ marginTop: "20px" }}>
          <h3>âœ… Result Summary</h3>
          <ul>
            <li>ðŸ”´ Back not straight: {result.posture_feedback.back_not_straight}</li>
            <li>ðŸ”´ Neck bent more than 30Â°: {result.posture_feedback.neck_bent_more_than_30}</li>
            <li>ðŸ”´ Knees beyond toes: {result.posture_feedback.knees_beyond_toes}</li>
            <li>ðŸ”´ Back angle &lt; 150Â°: {result.posture_feedback.back_angle_less_than_150}</li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default WebcamCapture;
