// components/WebcamCapture.js
import React, { useRef, useState } from 'react';

function WebcamCapture() {
  const videoRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const [recordedChunks, setRecordedChunks] = useState([]);
  const [result, setResult] = useState(null);
  const [recording, setRecording] = useState(false);

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    videoRef.current.srcObject = stream;

    const mediaRecorder = new MediaRecorder(stream, { mimeType: "video/webm" });
    mediaRecorderRef.current = mediaRecorder;

    mediaRecorder.ondataavailable = event => {
      if (event.data.size > 0) setRecordedChunks(prev => [...prev, event.data]);
    };

    mediaRecorder.onstop = async () => {
      const blob = new Blob(recordedChunks, { type: "video/webm" });
      const formData = new FormData();
      formData.append("file", blob, "webcam_video.webm");

      const res = await fetch("http://localhost:54301/analyze", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      setResult(data);

      // stop stream
      stream.getTracks().forEach(track => track.stop());
    };

    setRecordedChunks([]);
    setRecording(true);
    mediaRecorder.start();
  };

  const stopRecording = () => {
    mediaRecorderRef.current.stop();
    setRecording(false);
  };

  return (
    <div>
      <h2>Webcam Posture Analysis</h2>
      <video ref={videoRef} autoPlay muted width={400} height={300} />
      {!recording && <button onClick={startRecording}>Start Recording</button>}
      {recording && <button onClick={stopRecording}>Stop & Analyze</button>}
      {result && (
        <div>
          <h4>Result Summary</h4>
          <ul>
            {result.summary.map((item, i) => (
              <li key={i}>{item.issue} â€” {item.count} times</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default WebcamCapture;
