// components/UploadVideo.js
import React, { useState } from 'react';

const UploadVideo = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [result, setResult] = useState(null);

  const handleUpload = async () => {
    if (!selectedFile) return alert("Please select a video");

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      const response = await fetch("http://localhost:54301/analyze", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setResult(data);
    } catch (err) {
      alert("Upload failed: " + err.message);
    }
  };

  return (
    <div>
      <h2>Upload Video for Analysis</h2>
      <input type="file" accept="video/*" onChange={e => setSelectedFile(e.target.files[0])} />
      <button onClick={handleUpload}>Analyze</button>
      {result && (
        <div>
          <h4>Result Summary</h4>
          <ul>
            {result.summary.map((item, i) => (
              <li key={i}>{item.issue} â€” {item.count} times</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default UploadVideo;
