import React, { useRef, useState } from "react";

function App() {
  const mediaRecorderRef = useRef(null);
  const [recording, setRecording] = useState(false);
  const [videoBlob, setVideoBlob] = useState(null);
  const [feedback, setFeedback] = useState([]);
  const [summary, setSummary] = useState([]);

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    mediaRecorderRef.current = new MediaRecorder(stream);

    const chunks = [];
    mediaRecorderRef.current.ondataavailable = (event) => {
      if (event.data.size > 0) chunks.push(event.data);
    };

    mediaRecorderRef.current.onstop = () => {
      const blob = new Blob(chunks, { type: "video/mp4" });
      setVideoBlob(blob);
      uploadVideo(blob); // ‚¨ÖÔ∏è Upload to FastAPI
    };

    mediaRecorderRef.current.start();
    setRecording(true);
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
    } else {
      console.warn("MediaRecorder is not initialized.");
    }
    setRecording(false);
  };

  const uploadVideo = async (blob) => {
    const file = new File([blob], "recorded_video.mp4", { type: "video/mp4" });

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch("http://localhost:8000/analyze", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error("Upload failed");

      const data = await response.json();
      setFeedback(data.feedback);
      setSummary(data.summary);
    } catch (error) {
      console.error("Error uploading video:", error);
    }
  };

  return (
    <div>
      <h1>üìπ Bad Posture Detection App</h1>
      <button onClick={startRecording} disabled={recording}>
        Start Recording
      </button>
      <button onClick={stopRecording} disabled={!recording}>
        Stop Recording
      </button>

      {videoBlob && (
        <div>
          <h3>üéûÔ∏è Recorded Video:</h3>
          <video src={URL.createObjectURL(videoBlob)} controls width="400" />
        </div>
      )}

      {summary.length > 0 && (
        <div>
          <h3>üìù Posture Analysis Summary</h3>
          <ul>
            {summary.map((item, idx) => (
              <li key={idx}>
                {item.issue}: {item.count} times
              </li>
            ))}
          </ul>
        </div>
      )}

      {feedback.length > 0 && (
        <div>
          <h3>üìç Detailed Feedback by Frame</h3>
          <ul>
            {feedback.map((f, idx) => (
              <li key={idx}>
                Frame {f.frame}: {f.issues.join(", ")}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
