import React, { useState } from 'react';
import axios from '../api';

function UploadVideo() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleUpload = async () => {
    if (!file) return alert("Please select a video first");

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    try {
      const response = await axios.post('/analyze', formData);
      setResult(response.data);
    } catch (error) {
      console.error("Error uploading video", error);
      alert("Upload failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <input type="file" accept="video/*,image/*" onChange={(e) => setFile(e.target.files[0])} />
      <button onClick={handleUpload}>Upload & Analyze</button>

      {loading && <p>Analyzing video... please wait</p>}

      {result && (
        <div>
          <h3>Feedback:</h3>
          {result.feedback.map((frameData, index) => (
            <div key={index}>
              <h4>Frame {frameData.frame}</h4>
              <ul>
                {frameData.issues.map((issue, idx) => (
                  <li key={idx} style={{ color: "red" }}>{issue}</li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default UploadVideo;
